import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './MovieDetails.css'; // Assicurati di avere il percorso corretto
import { ClipLoader } from 'react-spinners';

// La tua API Key
const API_KEY = '6953698b';

const MovieDetails = () => {
    const { movieId } = useParams();
    const [movieDetails, setMovieDetails] = useState(null);
    const [comments, setComments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [newComment, setNewComment] = useState("");  // Stato per il nuovo commento
    const [rating, setRating] = useState(0); // Stato per la valutazione

    useEffect(() => {
        const fetchMovieDetails = async () => {
            try {
                // Fetch dei dettagli del film
                const movieResponse = await fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&i=${movieId}`);
                if (!movieResponse.ok) {
                    throw new Error('Failed to fetch movie details');
                }
                const movieData = await movieResponse.json();

                if (movieData.Response === "True") {
                    setMovieDetails(movieData);
                } else {
                    throw new Error(movieData.Error);
                }

                // Fetch dei commenti
                const commentsResponse = await fetch(`https://striveschool-api.herokuapp.com/api/comments/${movieId}`, {
                    headers: {
                        'Authorization': `Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2NmNmMjA2MjAwOGQxMDAwMTVkZDNiNGUiLCJpYXQiOjE3MjQ4NTAyNzQsImV4cCI6MTcyNjA1OTg3NH0.KFK9aex6ISFoXbsSZTo8JIeub5YxMMR-UgydUsklpAs`
                    }
                });
                if (!commentsResponse.ok) {
                    if (commentsResponse.status === 401) {
                        throw new Error("Unauthorized access - Please check your token.");
                    } else {
                        throw new Error('Failed to fetch comments');
                    }
                }
                const commentsData = await commentsResponse.json();
                setComments(commentsData);

            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchMovieDetails();
    }, [movieId]);

    // Funzione per gestire l'invio del nuovo commento
    const handleAddComment = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`https://striveschool-api.herokuapp.com/api/comments/${movieId}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer <YOUR_API_TOKEN>`
                },
                body: JSON.stringify({
                    comment: newComment,
                    rate: rating
                })
            });

            if (!response.ok) {
                throw new Error('Failed to add comment');
            }

            // Aggiungi il nuovo commento alla lista dei commenti
            const addedComment = await response.json();
            setComments([...comments, addedComment]);
            setNewComment(""); // Resetta il campo del commento
            setRating(0); // Resetta la valutazione
        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div className="movie-details-container">
            {loading ? (
                <div className="movie-details-loading">
                    <ClipLoader color="#ffffff" loading={loading} size={50} />
                </div>
            ) : error ? (
                <div className="movie-details-error">
                    <p>Error: {error}</p>
                </div>
            ) : (
                <div className="movie-details-content">
                    <img
                        src={movieDetails?.Poster}
                        alt={movieDetails?.Title}
                        className="movie-details-poster"
                    />
                    <div className="movie-details-info">
                        <h1>{movieDetails?.Title}</h1>
                        <p><strong>Year:</strong> {movieDetails?.Year}</p>
                        <p><strong>Genre:</strong> {movieDetails?.Genre}</p>
                        <p><strong>Director:</strong> {movieDetails?.Director}</p>
                        <p><strong>Actors:</strong> {movieDetails?.Actors}</p>
                        <p><strong>Plot:</strong> {movieDetails?.Plot}</p>
                        <p><strong>Rating:</strong> {movieDetails?.imdbRating}</p>
                    </div>
                    <div className="movie-details-comments">
                        <h2>Comments</h2>
                        {comments.length > 0 ? comments.map(comment => (
                            <div key={comment._id} className="comment-card">
                                <p className="comment-author">{comment.comment}</p>
                                <p className="comment-rating">Rating: {comment.rate}</p>
                            </div>
                        )) : (
                            <p>No comments yet</p>
                        )}
                        <form onSubmit={handleAddComment} className="comment-form">
                            <textarea
                                value={newComment}
                                onChange={(e) => setNewComment(e.target.value)}
                                placeholder="Add your comment here"
                                required
                            />
                            <div className="rating-container">
                                <label>Rating:</label>
                                <input
                                    type="number"
                                    value={rating}
                                    onChange={(e) => setRating(parseInt(e.target.value))}
                                    min="1"
                                    max="10"
                                    required
                                />
                            </div>
                            <button type="submit">Add Comment</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MovieDetails;
