import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { ClipLoader } from 'react-spinners';

const API_KEY = '6953698b';
const COMMENTS_API = 'https://striveschool-api.herokuapp.com/api/comments'; // Endpoint per i commenti

function MovieDetails() {
    const { movieId } = useParams(); // Ottiene l'ID del film dall'URL
    const [movieDetails, setMovieDetails] = useState(null);
    const [comments, setComments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Fetch per i dettagli del film da OMDB API
        const fetchMovieDetails = async () => {
            try {
                const response = await fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&i=${movieId}`);
                const data = await response.json();
                if (data.Response === 'True') {
                    setMovieDetails(data);
                } else {
                    throw new Error(data.Error);
                }
            } catch (err) {
                setError(err.message);
            }
        };

        // Fetch per i commenti dalle API interne
        const fetchComments = async () => {
            try {
                const response = await fetch(`${COMMENTS_API}/${movieId}`, {
                    headers: {
                        'Authorization': 'Bearer <YOUR_TOKEN_HERE>' // Aggiungi il token qui
                    }
                });
                const data = await response.json();
                setComments(data);
            } catch (err) {
                setError('Errore nel recuperare i commenti');
            }
        };

        setLoading(true);
        Promise.all([fetchMovieDetails(), fetchComments()])
            .finally(() => setLoading(false));
    }, [movieId]);

    if (loading) {
        return (
            <div className="loader-container">
                <ClipLoader color="#ffffff" loading={loading} size={50} />
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-message">
                <p>Error: {error}</p>
            </div>
        );
    }

    return (
        <div style={{ padding: '20px' }}>
            {movieDetails && (
                <>
                    <h2>{movieDetails.Title}</h2>
                    <img src={movieDetails.Poster} alt={movieDetails.Title} style={{ width: '200px' }} />
                    <p><strong>Plot:</strong> {movieDetails.Plot}</p>
                    <p><strong>Year:</strong> {movieDetails.Year}</p>
                    <p><strong>Genre:</strong> {movieDetails.Genre}</p>
                </>
            )}

            <div>
                <h3>Comments</h3>
                {comments.length > 0 ? (
                    comments.map((comment, index) => (
                        <div key={index}>
                            <p><strong>{comment.author}:</strong> {comment.comment}</p>
                        </div>
                    ))
                ) : (
                    <p>No comments available.</p>
                )}
            </div>
        </div>
    );
}

export default MovieDetails;
